import { Fonts } from '../../fonts';

function getStyles(typescale, viewport) {
  return MicroConfig[typescale][viewport];
}

var MicroConfig = {
  VDS: {
    mobile: {
      fontSize: 11,
      lineHeight: 16,
      fontWeight: 'normal',
      fontFamily: Fonts.VerizonNHGeTX
    },
    desktop: {
      fontSize: 11,
      lineHeight: 16,
      fontWeight: 'normal',
      fontFamily: Fonts.VerizonNHGeTX
    }
  },
  getStyles: getStyles
};
export default MicroConfig;
