import { Fonts } from '../../fonts';

function getStyles(typescale, viewport) {
  return SubtitleConfig[typescale][viewport];
}

var SubtitleConfig = {
  VDS: {
    mobile: {
      fontSize: 20,
      lineHeight: 24,
      fontWeight: 'normal',
      fontFamily: Fonts.VerizonNHGeDS
    },
    desktop: {
      fontSize: 20,
      lineHeight: 24,
      fontWeight: 'normal',
      fontFamily: Fonts.VerizonNHGeDS
    }
  },
  getStyles: getStyles
};
export default SubtitleConfig;
