import React from 'react';
import { TextLinkCaret as CoreTextLinkCaret } from '@vds-core/buttons';
import { BodyConfig, TypographyConfig } from '@vds/typography';
import { IconData } from '@vds/icons';
import { colors } from '@vds/theme';

const TextLinkCaret = props => {
  let typescale = TypographyConfig.getTypescale();

  return (
    <CoreTextLinkCaret
      {...props}
      useIcon
      size="large"
      calculateFontWeight={700}
      padding="0"
      hitArea
      hoverState
      focusState
      activeState
      size="large"
      typescale={typescale}
      fontConfig={BodyConfig}
      iconConfig={IconData}
      colorConfig={colors}
    />
  );
};

export default TextLinkCaret;
