import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import IconCore from '@vds-core/icons';
import IconData from './data.json';

const propTypes = {
  /**
   * JSON File with paths for new Icons for 1.0.
   */
  data: PropTypes.object,
  /**
   * Boolean to change Icon stroke from default of 1px to 2px.
   */
  medium: PropTypes.bool,
};

const defaultProps = {
  data: IconData,
  medium: false,
};

const Icon = props => {
  return (
    <IconCore
      {...props}
      data={IconData}
      hasStroke
      medium={props.medium}
      focusState={false}
    />
  );
};

Icon.propTypes = propTypes;
Icon.defaultProps = defaultProps;

export default Icon;
