import React, { Component } from 'react';
import { Tab as CoreTab } from '@vds-core/tabs';
import { TitleConfig, TypographyConfig } from '@vds/typography';
import { calculateRem } from '@uie/javascript-style-helpers';

const typescale = TypographyConfig.getTypescale();

const Tab = props => {
  return (
    <CoreTab
      {...props}
      focusState
      minHeight
      tabSpacing={calculateRem(0, 24, 0, 0)}
      titleConfig={TitleConfig}
      typescale={typescale}
    />
  );
};
export default Tab;
