import React, { Component } from 'react';
import { Tabs as CoreTabs } from '@vds-core/tabs';
import { TitleConfig, TypographyConfig } from '@vds/typography';
import { colors } from '@vds/theme';

const typescale = TypographyConfig.getTypescale();

const Tabs = props => {
  return (
    <CoreTabs
      {...props}
      focusState
      minHeight
      colorConfig={colors}
      titleConfig={TitleConfig}
      typescale={typescale}
    />
  );
};
export default Tabs;
