import React from 'react';
import { Tooltip as CoreTooltip } from '@vds-core/tooltips';
import { BodyConfig, TypographyConfig } from '@vds/typography';
import { IconData } from '@vds/icons';
import { colors } from '@vds/theme';
import { calculateRem } from '@uie/javascript-style-helpers';

const Tooltip = props => {
  return (
    <CoreTooltip
      {...props}
      focusState
      hoverState
      activeState
      iconConfig={IconData}
      bodyConfig={BodyConfig}
      colorConfig={colors}
      typescale={TypographyConfig.getTypescale()}
      dialogPadding={calculateRem(12, 0, 12, 12)}
    />
  );
};

export default Tooltip;
