import React from 'react';
import PropTypes from 'prop-types';
import { Body as CoreBody } from '@vds-core/typography';
import { TypographyConfig } from '../../index';
import BodyConfig from './config';
import { Fonts } from '../../fonts';

const propTypes = {
  size: PropTypes.oneOf(['small', 'large']),
};

const defaultProps = {
  size: 'small',
};

const Body = props => {
  function getFontWeight(typescale, bold) {
    return bold ? '700' : '400';
  }

  return (
    <CoreBody
      {...props}
      getFontWeight={getFontWeight}
      typescale={TypographyConfig.getTypescale()}
      config={BodyConfig}
      size={props.size}
      fontsConfig={Fonts}
    />
  );
};

Body.propTypes = propTypes;
Body.defaultProps = defaultProps;
export default Body;
