import React from 'react';
import { Micro as CoreMicro } from '@vds-core/typography';
import { TypographyConfig } from '../../index';
import MicroConfig from './config';
import { Fonts } from '../../fonts';

const Micro = props => {
  return (
    <CoreMicro
      {...props}
      typescale={TypographyConfig.getTypescale()}
      config={MicroConfig}
      fontsConfig={Fonts}
    />
  );
};

export default Micro;
