import React from 'react';
import { Title as CoreTitle } from '@vds-core/typography';
import TitleConfig from './config';
import { TypographyConfig } from '../../index';
import PropTypes from 'prop-types';
import { Fonts } from '../../fonts';

const propTypes = {
  size: PropTypes.oneOf([
    'XSmall',
    'small',
    'medium',
    'large',
    'XLarge',
    '2XLarge',
  ]),
};

const Title = props => {
  function getTitleFontWeight(typescale, bold, size) {
    //always bold
    return 700;
  }
  return (
    <CoreTitle
      {...props}
      size={props.size}
      typescale={TypographyConfig.getTypescale()}
      getFontWeight={getTitleFontWeight}
      config={TitleConfig}
      fontsConfig={Fonts}
    />
  );
};
export default Title;
